        var logoSrc = 'data:image/png;base64,';
        
        function extractParticles(img, maxSize, scale) {
            var canvas = document.createElement('canvas');
            var ctx = canvas.getContext('2d');
            
            var w = img.width;
            var h = img.height;
            var ratio = Math.min(maxSize / w, maxSize / h);
            canvas.width = w * ratio;
            canvas.height = h * ratio;
            
            ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
            
            var imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
            var pixels = imageData.data;
            var particles = [];
            var gap = particleGap;
            
            for (var y = 0; y < canvas.height; y += gap) {
                for (var x = 0; x < canvas.width; x += gap) {
                    var i = (y * canvas.width + x) * 4;
                    var r = pixels[i];
                    var g = pixels[i + 1];
                    var b = pixels[i + 2];
                    var a = pixels[i + 3];
                    
                    var isWhite = r > 254 && g > 254 && b > 254;
                    var hasColor = (r < 255 || g < 255 || b < 255);

                    if (!isWhite && hasColor && a > 20) {
                        // Farben intensiver machen - helle Pixel stÃ¤rker abdunkeln
                        var brightness = (r + g + b) / 3 / 255;
                        var intensity = 0.55 - (brightness * 0.27);
                        // T (ganz rechts) etwas dunkler machen
                        var xNorm = x / canvas.width;
                        if (xNorm > 0.9) {
                            intensity *= 0.85;
                        }
                        particles.push({
                            x: (x - canvas.width / 2) * scale,
                            y: -(y - canvas.height / 2) * scale,
                            z: 0,
                            r: (r / 255) * intensity,
                            g: (g / 255) * intensity,
                            b: (b / 255) * intensity
                        });
                    }
                }
            }
            
            return particles;
        }
        
        // Mobile Detection & Scaling
        var isMobile = window.innerWidth < 768;
        var mobileScale = isMobile ? 0.5 : 1.0;
        var particleGap = isMobile ? 2 : 1;
        var particleMaxSize = isMobile ? 500 : 1000;
        var particleScale = isMobile ? 0.035 : 0.06;
        var cameraZ = isMobile ? 55 : 100;
        // Logo: auf Mobile oben rechts neben "Wir sind", kleiner
        var logoOffsetX = isMobile ? 20 : -64;
        var logoOffsetY = isMobile ? 22 : 33;
        // Icons (Globe, DNA, Cloud, Scatter): auf Mobile unten mittig
        var iconCenterX = isMobile ? 0 : 55;

        var img = new Image();
        img.onload = function() {
            var loadingEl = document.getElementById('loading'); if (loadingEl) loadingEl.style.display = 'none';
            var particleData = extractParticles(img, particleMaxSize, particleScale);
            initScene(particleData);
        };
        img.src = logoSrc;

        function initScene(particleData) {
            var container = document.getElementById('canvas-container');
            var scene = new THREE.Scene();
            var camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            var renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
            
            renderer.setSize(window.innerWidth, window.innerHeight);
            renderer.setPixelRatio(Math.min(window.devicePixelRatio, isMobile ? 1.5 : 2));
            renderer.setClearColor(0x000000, 0);
            container.appendChild(renderer.domElement);

            camera.position.z = cameraZ;
            
            var totalParticles = particleData.length;
            var particleGeometry = new THREE.BufferGeometry();
            
            var currentPositions = new Float32Array(totalParticles * 3);
            var currentColors = new Float32Array(totalParticles * 3);
            var targetPositions = new Float32Array(totalParticles * 3);
            var targetColors = new Float32Array(totalParticles * 3);
            var startPositions = new Float32Array(totalParticles * 3);
            var startColors = new Float32Array(totalParticles * 3);
            var sizes = new Float32Array(totalParticles);
            var startSizes = new Float32Array(totalParticles);
            var targetSizes = new Float32Array(totalParticles);
            var delays = new Float32Array(totalParticles);

            for (var i = 0; i < totalParticles; i++) {
                var data = particleData[i];
                
                targetPositions[i * 3] = data.x + logoOffsetX;
                targetPositions[i * 3 + 1] = data.y + logoOffsetY;
                targetPositions[i * 3 + 2] = (Math.random() - 0.5) * 0.1;
                targetColors[i * 3] = data.r;
                targetColors[i * 3 + 1] = data.g;
                targetColors[i * 3 + 2] = data.b;

                startPositions[i * 3] = data.x + logoOffsetX;
                startPositions[i * 3 + 1] = data.y + logoOffsetY;
                startPositions[i * 3 + 2] = (Math.random() - 0.5) * 0.1;
                startColors[i * 3] = data.r;
                startColors[i * 3 + 1] = data.g;
                startColors[i * 3 + 2] = data.b;

                currentPositions[i * 3] = startPositions[i * 3];
                currentPositions[i * 3 + 1] = startPositions[i * 3 + 1];
                currentPositions[i * 3 + 2] = startPositions[i * 3 + 2];
                currentColors[i * 3] = startColors[i * 3];
                currentColors[i * 3 + 1] = startColors[i * 3 + 1];
                currentColors[i * 3 + 2] = startColors[i * 3 + 2];

                var baseSize = Math.random() * 0.15 + 0.85;
                sizes[i] = baseSize;
                startSizes[i] = baseSize;
                targetSizes[i] = baseSize;
            }

            particleGeometry.setAttribute('position', new THREE.BufferAttribute(currentPositions, 3));
            particleGeometry.setAttribute('color', new THREE.BufferAttribute(currentColors, 3));
            particleGeometry.setAttribute('size', new THREE.BufferAttribute(sizes, 1));

            var vertexShader = [
                'attribute float size;',
                'attribute vec3 color;',
                'varying vec3 vColor;',
                'uniform float pixelRatio;',
                'void main() {',
                '    vColor = color;',
                '    vec4 mvPosition = modelViewMatrix * vec4(position, 1.0);',
                '    gl_PointSize = size * pixelRatio * (150.0 / -mvPosition.z);',
                '    gl_Position = projectionMatrix * mvPosition;',
                '}'
            ].join('\n');

            var fragmentShader = [
                'varying vec3 vColor;',
                'void main() {',
                '    float dist = length(gl_PointCoord - vec2(0.5));',
                '    if (dist > 0.5) discard;',
                '    float glow = 1.0 - smoothstep(0.0, 0.4, dist);',
                '    glow = pow(glow, 1.2);',
                '    vec3 finalColor = vColor * glow * 1.6;',
                '    float alpha = min(glow * 1.5, 1.0);',
                '    gl_FragColor = vec4(finalColor, alpha);',
                '}'
            ].join('\n');

            var particleMaterial = new THREE.ShaderMaterial({
                uniforms: { pixelRatio: { value: renderer.getPixelRatio() } },
                vertexShader: vertexShader,
                fragmentShader: fragmentShader,
                transparent: true,
                blending: THREE.AdditiveBlending,
                depthWrite: false
            });

            var particles = new THREE.Points(particleGeometry, particleMaterial);
            scene.add(particles);

            var animationProgress = 1;
            var animationDuration = 3;
            var animationPhase = 'toShape';
            var pauseTimer = 0;
            var pauseDuration = 1;
            var globeCenterX = iconCenterX;
            var globeRotation = 0;

            function easeInOutCubic(t) {
                return t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;
            }

            var clock = new THREE.Clock();
            var positionsAttr = particleGeometry.attributes.position;
            var colorsAttr = particleGeometry.attributes.color;
            var sizesAttr = particleGeometry.attributes.size;

            function formGlobeIcon() {
                var globeRadius = isMobile ? 18 : 28;

                for (var i = 0; i < totalParticles; i++) {
                    startPositions[i * 3] = currentPositions[i * 3];
                    startPositions[i * 3 + 1] = currentPositions[i * 3 + 1];
                    startPositions[i * 3 + 2] = currentPositions[i * 3 + 2];
                    startColors[i * 3] = currentColors[i * 3];
                    startColors[i * 3 + 1] = currentColors[i * 3 + 1];
                    startColors[i * 3 + 2] = currentColors[i * 3 + 2];
                    startSizes[i] = sizes[i];

                    var t = i / totalParticles;
                    var x, y, z;

                    if (t < 0.15) {
                        // Aequator - horizontaler Ring in der Mitte
                        var angle = (t / 0.15) * Math.PI * 2;
                        x = Math.cos(angle) * globeRadius;
                        y = 0;
                        z = Math.sin(angle) * globeRadius;
                    } else if (t < 0.25) {
                        // Breitengrad oben (30 Grad)
                        var angle = ((t - 0.15) / 0.10) * Math.PI * 2;
                        var latRadius = globeRadius * Math.cos(Math.PI / 6);
                        var latY = globeRadius * Math.sin(Math.PI / 6);
                        x = Math.cos(angle) * latRadius;
                        y = latY;
                        z = Math.sin(angle) * latRadius;
                    } else if (t < 0.35) {
                        // Breitengrad unten (-30 Grad)
                        var angle = ((t - 0.25) / 0.10) * Math.PI * 2;
                        var latRadius = globeRadius * Math.cos(Math.PI / 6);
                        var latY = -globeRadius * Math.sin(Math.PI / 6);
                        x = Math.cos(angle) * latRadius;
                        y = latY;
                        z = Math.sin(angle) * latRadius;
                    } else if (t < 0.45) {
                        // Breitengrad weit oben (60 Grad)
                        var angle = ((t - 0.35) / 0.10) * Math.PI * 2;
                        var latRadius = globeRadius * Math.cos(Math.PI / 3);
                        var latY = globeRadius * Math.sin(Math.PI / 3);
                        x = Math.cos(angle) * latRadius;
                        y = latY;
                        z = Math.sin(angle) * latRadius;
                    } else if (t < 0.55) {
                        // Breitengrad weit unten (-60 Grad)
                        var angle = ((t - 0.45) / 0.10) * Math.PI * 2;
                        var latRadius = globeRadius * Math.cos(Math.PI / 3);
                        var latY = -globeRadius * Math.sin(Math.PI / 3);
                        x = Math.cos(angle) * latRadius;
                        y = latY;
                        z = Math.sin(angle) * latRadius;
                    } else if (t < 0.67) {
                        // Laengengrad 1 - vertikaler Ring (0 Grad)
                        var angle = ((t - 0.55) / 0.12) * Math.PI * 2;
                        x = 0;
                        y = Math.sin(angle) * globeRadius;
                        z = Math.cos(angle) * globeRadius;
                    } else if (t < 0.79) {
                        // Laengengrad 2 - vertikaler Ring (90 Grad gedreht)
                        var angle = ((t - 0.67) / 0.12) * Math.PI * 2;
                        x = Math.cos(angle) * globeRadius;
                        y = Math.sin(angle) * globeRadius;
                        z = 0;
                    } else if (t < 0.91) {
                        // Laengengrad 3 - schraeg (45 Grad)
                        var angle = ((t - 0.79) / 0.12) * Math.PI * 2;
                        var lngAngle = Math.PI / 4;
                        x = Math.cos(angle) * globeRadius * Math.sin(lngAngle);
                        y = Math.sin(angle) * globeRadius;
                        z = Math.cos(angle) * globeRadius * Math.cos(lngAngle);
                    } else {
                        // Laengengrad 4 - schraeg (-45 Grad)
                        var angle = ((t - 0.91) / 0.09) * Math.PI * 2;
                        var lngAngle = -Math.PI / 4;
                        x = Math.cos(angle) * globeRadius * Math.sin(lngAngle);
                        y = Math.sin(angle) * globeRadius;
                        z = Math.cos(angle) * globeRadius * Math.cos(lngAngle);
                    }

                    targetPositions[i * 3] = x + globeCenterX;
                    targetPositions[i * 3 + 1] = y;
                    targetPositions[i * 3 + 2] = z;

                    // Farben: Blau/Cyan
                    var colorT = Math.random();
                    if (colorT < 0.5) {
                        targetColors[i * 3] = 0.1;
                        targetColors[i * 3 + 1] = 0.4;
                        targetColors[i * 3 + 2] = 0.9;
                    } else {
                        targetColors[i * 3] = 0.0;
                        targetColors[i * 3 + 1] = 0.5;
                        targetColors[i * 3 + 2] = 1.0;
                    }

                    targetSizes[i] = Math.random() * 0.4 + 2.0;
                    delays[i] = 0;
                }
            }


            // DNA Helix Variablen
            var dnaRotation = 0;
            var dnaCenterX = iconCenterX;

            function formDNAHelix() {
                // DNA Doppelhelix - EXAKT wie Referenzbild:
                // Unten: dichter Kern zusammenlaufend
                // Mitte + Oben: klassische Doppelhelix mit Querverbindungen ueber gesamte Hoehe
                // Oben: aufbluehend in zwei Richtungen, sich aufloesend

                var helixHeight = isMobile ? 55 : 90;
                var baseRadius = isMobile ? 5 : 8;
                var topRadius = isMobile ? 14 : 22;
                var turns = 2.5;
                var zDepth = isMobile ? 14 : 22;

                // Partikel-Verteilung: 38% Strang 1, 38% Strang 2, 24% Verbindungen
                var strand1End = 0.38;
                var strand2End = 0.76;

                for (var i = 0; i < totalParticles; i++) {
                    startPositions[i * 3] = currentPositions[i * 3];
                    startPositions[i * 3 + 1] = currentPositions[i * 3 + 1];
                    startPositions[i * 3 + 2] = currentPositions[i * 3 + 2];
                    startColors[i * 3] = currentColors[i * 3];
                    startColors[i * 3 + 1] = currentColors[i * 3 + 1];
                    startColors[i * 3 + 2] = currentColors[i * 3 + 2];
                    startSizes[i] = sizes[i];

                    var t = i / totalParticles;
                    var x, y, z;
                    var particleSize = 2.0;
                    var alpha = 1.0;

                    if (t < strand1End) {
                        // Erster Strang
                        var strandT = t / strand1End;

                        // Y-Position: unten = -45, oben = +45
                        y = (strandT - 0.5) * helixHeight;

                        // Nicht-linearer Taper: unten eng, oben weit aufgespreizt
                        var taperFactor = Math.pow(strandT, 0.6);
                        var currentRadius = baseRadius + (topRadius - baseRadius) * taperFactor;
                        var currentZ = zDepth * (0.4 + taperFactor * 0.6);

                        // Spiralwinkel
                        var angle = strandT * Math.PI * 2 * turns;

                        // Basis-Position
                        var baseX = Math.cos(angle) * currentRadius;
                        var baseZ = Math.sin(angle) * currentZ;

                        // Streuung: unten minimal, oben stark (Aufloesung)
                        var scatterAmount = 1.0 + Math.pow(strandT, 2) * 12;
                        var scatter = (Math.random() - 0.5) * scatterAmount;
                        var scatterZ = (Math.random() - 0.5) * scatterAmount * 0.6;
                        var scatterY = (Math.random() - 0.5) * scatterAmount * 0.4;

                        x = baseX + scatter;
                        z = baseZ + scatterZ;
                        y = y + scatterY;

                        // Groesse: kleiner fuer weniger Ueberlappung
                        particleSize = 2.0 - strandT * 0.6;

                        // Intensitaet: dunkler fuer sattes Lila ohne Weiss
                        alpha = 0.7 - strandT * 0.2;

                    } else if (t < strand2End) {
                        // Zweiter Strang - 180 Grad versetzt
                        var strandT = (t - strand1End) / (strand2End - strand1End);

                        y = (strandT - 0.5) * helixHeight;

                        var taperFactor = Math.pow(strandT, 0.6);
                        var currentRadius = baseRadius + (topRadius - baseRadius) * taperFactor;
                        var currentZ = zDepth * (0.4 + taperFactor * 0.6);

                        // Zweiter Strang ist 180 Grad versetzt
                        var angle = strandT * Math.PI * 2 * turns + Math.PI;

                        var baseX = Math.cos(angle) * currentRadius;
                        var baseZ = Math.sin(angle) * currentZ;

                        var scatterAmount = 1.0 + Math.pow(strandT, 2) * 12;
                        var scatter = (Math.random() - 0.5) * scatterAmount;
                        var scatterZ = (Math.random() - 0.5) * scatterAmount * 0.6;
                        var scatterY = (Math.random() - 0.5) * scatterAmount * 0.4;

                        x = baseX + scatter;
                        z = baseZ + scatterZ;
                        y = y + scatterY;

                        particleSize = 2.0 - strandT * 0.6;
                        alpha = 0.7 - strandT * 0.2;

                    } else {
                        // Querverbindungen (Basenpaare) - UEBER GESAMTE HOEHE
                        var connT = (t - strand2End) / (1.0 - strand2End);
                        var numConnections = 20;  // Mehr Verbindungen
                        var connIndex = Math.floor(connT * numConnections);
                        var connFrac = (connT * numConnections) % 1;

                        // Verbindungen ueber gesamte Hoehe (0.05 bis 0.95)
                        var heightPos = 0.05 + (connIndex / numConnections) * 0.9;
                        y = (heightPos - 0.5) * helixHeight;

                        var taperFactor = Math.pow(heightPos, 0.6);
                        var currentRadius = baseRadius + (topRadius - baseRadius) * taperFactor;
                        var currentZ = zDepth * (0.4 + taperFactor * 0.6);

                        var baseAngle = heightPos * Math.PI * 2 * turns;

                        // Interpoliere zwischen beiden Straengen
                        var x1 = Math.cos(baseAngle) * currentRadius;
                        var z1 = Math.sin(baseAngle) * currentZ;
                        var x2 = Math.cos(baseAngle + Math.PI) * currentRadius;
                        var z2 = Math.sin(baseAngle + Math.PI) * currentZ;

                        x = x1 * (1 - connFrac) + x2 * connFrac;
                        z = z1 * (1 - connFrac) + z2 * connFrac;

                        // Streuung abhaengig von Hoehe - oben mehr
                        var connScatter = 1.5 + heightPos * 2;
                        x += (Math.random() - 0.5) * connScatter;
                        z += (Math.random() - 0.5) * connScatter;
                        y += (Math.random() - 0.5) * connScatter;

                        particleSize = 2.0 + (1 - heightPos) * 0.5;
                    }

                    // Leichte Schraegstellung der DNA (Rotation um Z-Achse)
                    var tiltAngle = 0.18; // ca. 10 Grad Neigung
                    var cosT = Math.cos(tiltAngle);
                    var sinT = Math.sin(tiltAngle);
                    var tiltedX = x * cosT - y * sinT;
                    var tiltedY = x * sinT + y * cosT;

                    targetPositions[i * 3] = tiltedX + dnaCenterX;
                    targetPositions[i * 3 + 1] = tiltedY;
                    targetPositions[i * 3 + 2] = z;

                    // Farbe: Sattes Lila/Violett - dunkler um Weiss zu vermeiden
                    var colorVariation = (Math.random() - 0.5) * 0.08;
                    targetColors[i * 3] = 0.45 + colorVariation;     // Rot (dunkler)
                    targetColors[i * 3 + 1] = 0.15 + colorVariation; // Gruen (dunkler)
                    targetColors[i * 3 + 2] = 0.95;                  // Blau (leicht reduziert)

                    // Partikelgroessen
                    targetSizes[i] = particleSize;

                    // Animation von unten nach oben
                    var normalizedY = (y + helixHeight * 0.5) / helixHeight;
                    delays[i] = normalizedY * 0.15;
                }
            }

            var cloudRotation = 0;
            var cloudCenterX = iconCenterX;
            var sphereRotation = 0;
            var sphereRotationSpeed = 0.6;
            var sphereCenterX = iconCenterX;

            function formCloudIcon() {
                var cs = mobileScale; // cloud scale
                for (var i = 0; i < totalParticles; i++) {
                    startPositions[i * 3] = currentPositions[i * 3];
                    startPositions[i * 3 + 1] = currentPositions[i * 3 + 1];
                    startPositions[i * 3 + 2] = currentPositions[i * 3 + 2];
                    startColors[i * 3] = currentColors[i * 3];
                    startColors[i * 3 + 1] = currentColors[i * 3 + 1];
                    startColors[i * 3 + 2] = currentColors[i * 3 + 2];
                    startSizes[i] = sizes[i];

                    var t = i / totalParticles;
                    var x, y, isGear = false;

                    if (t < 0.35) {
                        var cloudT = t / 0.35;
                        if (cloudT < 0.3) {
                            var angle = (cloudT / 0.3) * Math.PI * 2;
                            x = (Math.cos(angle) * 12 - 10) * cs;
                            y = (Math.sin(angle) * 10 + 8) * cs;
                        } else if (cloudT < 0.6) {
                            var angle = ((cloudT - 0.3) / 0.3) * Math.PI * 2;
                            x = Math.cos(angle) * 15 * cs;
                            y = (Math.sin(angle) * 12 + 10) * cs;
                        } else {
                            var angle = ((cloudT - 0.6) / 0.4) * Math.PI * 2;
                            x = (Math.cos(angle) * 12 + 10) * cs;
                            y = (Math.sin(angle) * 10 + 8) * cs;
                        }
                    } else if (t < 0.5) {
                        isGear = true;
                        var gearT = (t - 0.35) / 0.15;
                        var gearRadius = 8 * cs;
                        var teeth = 8;
                        var angle = gearT * Math.PI * 2;
                        var toothAngle = angle * teeth;
                        var toothOffset = (Math.sin(toothAngle) > 0 ? 2 * cs : 0);
                        x = Math.cos(angle) * (gearRadius + toothOffset);
                        y = Math.sin(angle) * (gearRadius + toothOffset) + 8 * cs;
                    } else if (t < 0.65) {
                        var laptopT = (t - 0.5) / 0.15;
                        if (laptopT < 0.6) {
                            var screenT = laptopT / 0.6;
                            if (screenT < 0.25) {
                                x = (-25 + screenT * 4 * 14) * cs;
                                y = -18 * cs;
                            } else if (screenT < 0.5) {
                                x = (-25 + 14) * cs;
                                y = (-18 + (screenT - 0.25) * 4 * 10) * cs;
                            } else if (screenT < 0.75) {
                                x = (-25 + 14 - (screenT - 0.5) * 4 * 14) * cs;
                                y = (-18 + 10) * cs;
                            } else {
                                x = -25 * cs;
                                y = (-18 + 10 - (screenT - 0.75) * 4 * 10) * cs;
                            }
                        } else {
                            var keyT = (laptopT - 0.6) / 0.4;
                            x = (-28 + keyT * 20) * cs;
                            y = -20 * cs;
                        }
                    } else if (t < 0.8) {
                        var phoneT = (t - 0.65) / 0.15;
                        if (phoneT < 0.25) {
                            x = (20 + phoneT * 4 * 8) * cs;
                            y = -22 * cs;
                        } else if (phoneT < 0.5) {
                            x = (20 + 8) * cs;
                            y = (-22 + (phoneT - 0.25) * 4 * 14) * cs;
                        } else if (phoneT < 0.75) {
                            x = (20 + 8 - (phoneT - 0.5) * 4 * 8) * cs;
                            y = (-22 + 14) * cs;
                        } else {
                            x = 20 * cs;
                            y = (-22 + 14 - (phoneT - 0.75) * 4 * 14) * cs;
                        }
                    } else {
                        var lineT = (t - 0.8) / 0.2;
                        if (lineT < 0.5) {
                            var lt = lineT * 2;
                            x = (-18 + lt * 18) * cs;
                            y = (-10 + lt * 10) * cs;
                        } else {
                            var lt = (lineT - 0.5) * 2;
                            x = (24 - lt * 24) * cs;
                            y = (-10 + lt * 10) * cs;
                        }
                    }

                    targetPositions[i * 3] = x + cloudCenterX;
                    targetPositions[i * 3 + 1] = y;
                    targetPositions[i * 3 + 2] = 0;

                    // Farben: Wolke blau/cyan, GerÃ¤te grau/weiÃ
                    if (t < 0.5) {
                        // Wolke und Zahnrad - Blau/Cyan
                        targetColors[i * 3] = 0.1;
                        targetColors[i * 3 + 1] = 0.4;
                        targetColors[i * 3 + 2] = 0.6;
                    } else if (t < 0.8) {
                        // GerÃ¤te - Grau/Silber
                        targetColors[i * 3] = 0.4;
                        targetColors[i * 3 + 1] = 0.4;
                        targetColors[i * 3 + 2] = 0.5;
                    } else {
                        // Verbindungslinien - Cyan
                        targetColors[i * 3] = 0.0;
                        targetColors[i * 3 + 1] = 0.5;
                        targetColors[i * 3 + 2] = 0.6;
                    }

                    targetSizes[i] = Math.random() * 0.3 + 2.5;
                    delays[i] = 0;
                }
            }

            function scatterParticles() {
                var minX = Infinity, maxX = -Infinity;
                for (var i = 0; i < totalParticles; i++) {
                    var x = currentPositions[i * 3];
                    if (x < minX) minX = x;
                    if (x > maxX) maxX = x;
                }
                var rangeX = maxX - minX;

                for (var i = 0; i < totalParticles; i++) {
                    startPositions[i * 3] = currentPositions[i * 3];
                    startPositions[i * 3 + 1] = currentPositions[i * 3 + 1];
                    startPositions[i * 3 + 2] = currentPositions[i * 3 + 2];
                    startColors[i * 3] = currentColors[i * 3];
                    startColors[i * 3 + 1] = currentColors[i * 3 + 1];
                    startColors[i * 3 + 2] = currentColors[i * 3 + 2];

                    var x = currentPositions[i * 3];
                    delays[i] = (1 - (x - minX) / rangeX) * 0.8;

                    var radius = (isMobile ? 16 : 25) + Math.random() * (isMobile ? 6 : 10);
                    var theta = Math.random() * Math.PI * 2;
                    var phi = Math.acos(2 * Math.random() - 1);

                    targetPositions[i * 3] = radius * Math.sin(phi) * Math.cos(theta) + iconCenterX;
                    targetPositions[i * 3 + 1] = radius * Math.sin(phi) * Math.sin(theta);
                    targetPositions[i * 3 + 2] = radius * Math.cos(phi);
                    var colorChoice = Math.random();
                    if (colorChoice < 0.35) {
                        // Blau mit Glow (dunkler)
                        targetColors[i * 3] = 0.2;
                        targetColors[i * 3 + 1] = 0.6;
                        targetColors[i * 3 + 2] = 1.8;
                    } else if (colorChoice < 0.60) {
                        // GrÃ¼n mit Glow (dunkler)
                        targetColors[i * 3] = 0.1;
                        targetColors[i * 3 + 1] = 1.5;
                        targetColors[i * 3 + 2] = 0.5;
                    } else if (colorChoice < 0.85) {
                        // Rot mit Glow (dunkler)
                        targetColors[i * 3] = 1.8;
                        targetColors[i * 3 + 1] = 0.15;
                        targetColors[i * 3 + 2] = 0.15;
                    } else {
                        // Magenta/Rosa mit Glow
                        targetColors[i * 3] = 3.0;
                        targetColors[i * 3 + 1] = 0.2;
                        targetColors[i * 3 + 2] = 2.5;
                    }

                    startSizes[i] = sizes[i];
                    // Nur 20% der Partikel anzeigen beim Scatter
                    if (Math.random() < 0.20) {
                        targetSizes[i] = Math.random() * 1.0 + 2.8;
                    } else {
                        targetSizes[i] = 0;
                    }
                }
            }

            function resetToShape() {
                // Finde min/max X fÃ¼r Buchstaben-Delays
                var minX = Infinity, maxX = -Infinity;
                for (var i = 0; i < totalParticles; i++) {
                    var x = particleData[i].x;
                    if (x < minX) minX = x;
                    if (x > maxX) maxX = x;
                }
                var rangeX = maxX - minX;

                for (var i = 0; i < totalParticles; i++) {
                    var data = particleData[i];

                    startPositions[i * 3] = currentPositions[i * 3];
                    startPositions[i * 3 + 1] = currentPositions[i * 3 + 1];
                    startPositions[i * 3 + 2] = currentPositions[i * 3 + 2];
                    startColors[i * 3] = currentColors[i * 3];
                    startColors[i * 3 + 1] = currentColors[i * 3 + 1];
                    startColors[i * 3 + 2] = currentColors[i * 3 + 2];

                    targetPositions[i * 3] = data.x + logoOffsetX;
                    targetPositions[i * 3 + 1] = data.y + logoOffsetY;
                    targetPositions[i * 3 + 2] = (Math.random() - 0.5) * 0.1;
                    targetColors[i * 3] = data.r;
                    targetColors[i * 3 + 1] = data.g;
                    targetColors[i * 3 + 2] = data.b;

                    startSizes[i] = sizes[i];
                    targetSizes[i] = Math.random() * 0.15 + 0.85;

                    // Delay basierend auf X-Position (links = W zuerst, rechts = T zuletzt)
                    delays[i] = ((data.x - minX) / rangeX) * 1.2;
                }
            }

            function animate() {
                requestAnimationFrame(animate);
                var delta = clock.getDelta();

                if (animationPhase === 'toShape') {
                    animationProgress += delta / (animationDuration * 0.8);
                    // Sphere weiter rotieren wÃ¤hrend Partikel zum Logo fliegen
                    if (animationProgress < 1.5) {
                        sphereRotation += delta * sphereRotationSpeed;
                    }
                    if (animationProgress >= 2.2) {
                        animationProgress = 2.2;
                        pauseTimer += delta;
                        if (pauseTimer >= pauseDuration) {
                            animationPhase = 'sphere';
                            animationProgress = 0;
                            pauseTimer = 0;
                            scatterParticles();
                        }
                    }
                } else if (animationPhase === 'sphere') {
                    animationProgress += delta / (animationDuration * 1.2);
                    // 3D Rotation startet sobald erste Partikel ankommen
                    if (animationProgress >= 0.5) {
                        sphereRotation += delta * sphereRotationSpeed;
                    }
                    if (animationProgress >= 1.8) {
                        animationProgress = 1.8;
                        pauseTimer += delta;
                        if (pauseTimer >= 0.3) {
                            animationPhase = 'toGlobe';
                            animationProgress = 0;
                            pauseTimer = 0;
                            sphereRotation = 0;
                            formGlobeIcon();
                        }
                    }
                } else if (animationPhase === 'toGlobe') {
                    animationProgress += delta / animationDuration;
                    // Globe Rotation - von Anfang an drehen
                    globeRotation += delta * 0.5;
                    // Sphere weiter rotieren wÃ¤hrend Partikel zum Globe fliegen
                    if (animationProgress < 0.8) {
                        sphereRotation += delta * sphereRotationSpeed;
                    }
                    if (animationProgress >= 1) {
                        animationProgress = 1;
                        pauseTimer += delta;
                        if (pauseTimer >= pauseDuration) {
                            animationPhase = 'scatterFromGlobe';
                            animationProgress = 0;
                            pauseTimer = 0;
                            scatterParticles();
                        }
                    }
                } else if (animationPhase === 'scatterFromGlobe') {
                    animationProgress += delta / (animationDuration * 1.2);
                    // Globe dreht sich weiter
                    globeRotation += delta * 0.5;
                    // 3D Rotation startet sobald erste Partikel ankommen
                    if (animationProgress >= 0.5) {
                        sphereRotation += delta * sphereRotationSpeed;
                    }
                    if (animationProgress >= 1.8) {
                        animationProgress = 1.8;
                        pauseTimer += delta;
                        if (pauseTimer >= 0.3) {
                            animationPhase = 'toDNA';
                            animationProgress = 0;
                            pauseTimer = 0;
                            sphereRotation = 0;
                            globeRotation = 0;
                            formDNAHelix();
                        }
                    }
                } else if (animationPhase === 'toDNA') {
                    animationProgress += delta / animationDuration;
                    // DNA Rotation - IMMER drehen (auch waehrend Pause)
                    dnaRotation += delta * 0.6;
                    if (animationProgress >= 1) {
                        animationProgress = 1;
                        pauseTimer += delta;
                        // Laengere Pause fuer DNA (2.5 Sekunden statt 1)
                        if (pauseTimer >= 2.5) {
                            // WICHTIG: Rotierte Positionen in currentPositions einrechnen vor scatter
                            // Das verhindert den Ruckler beim Uebergang
                            var tiltAngle = 0.18;
                            var cosT = Math.cos(tiltAngle);
                            var sinT = Math.sin(tiltAngle);
                            var cosR = Math.cos(dnaRotation);
                            var sinR = Math.sin(dnaRotation);
                            for (var j = 0; j < totalParticles; j++) {
                                var j3 = j * 3;
                                var relX = currentPositions[j3] - dnaCenterX;
                                var relY = currentPositions[j3+1];
                                var relZ = currentPositions[j3+2];
                                var untiltedX = relX * cosT + relY * sinT;
                                var untiltedY = -relX * sinT + relY * cosT;
                                var rotatedX = untiltedX * cosR - relZ * sinR;
                                var rotatedZ = untiltedX * sinR + relZ * cosR;
                                currentPositions[j3] = rotatedX * cosT - untiltedY * sinT + dnaCenterX;
                                currentPositions[j3+1] = rotatedX * sinT + untiltedY * cosT;
                                currentPositions[j3+2] = rotatedZ;
                            }
                            animationPhase = 'scatterFromDNA';
                            animationProgress = 0;
                            pauseTimer = 0;
                            dnaRotation = 0; // Zuruecksetzen da Rotation jetzt in Positionen ist
                            scatterParticles();
                        }
                    }
                } else if (animationPhase === 'scatterFromDNA') {
                    animationProgress += delta / (animationDuration * 1.2);
                    // DNA Rotation weiterlaufen lassen fuer Partikel die noch nicht losgeflogen sind
                    dnaRotation += delta * 0.6;
                    // Sphere Rotation - von Anfang an drehen (wie bei sphere nach Logo)
                    sphereRotation += delta * sphereRotationSpeed;
                    if (animationProgress >= 1.8) {
                        animationProgress = 1.8;
                        pauseTimer += delta;
                        if (pauseTimer >= 0.3) {
                            animationPhase = 'toShape';
                            animationProgress = 0;
                            pauseTimer = 0;
                            sphereRotation = 0;
                            dnaRotation = 0; // Erst hier zuruecksetzen
                            resetToShape();
                        }
                    }
                }

                for (var k = 0; k < totalParticles; k++) {
                    var k3 = k * 3;
                    var particleProgress;
                    if (animationPhase === 'sphere' || animationPhase === 'scatterFromGlobe' || animationPhase === 'scatterFromDNA' || animationPhase === 'toShape') {
                        particleProgress = Math.max(0, Math.min(1, animationProgress - delays[k]));
                    } else {
                        particleProgress = animationProgress;
                    }
                    var easedProgress = easeInOutCubic(particleProgress);

                    currentPositions[k3] = startPositions[k3] + (targetPositions[k3] - startPositions[k3]) * easedProgress;
                    currentPositions[k3+1] = startPositions[k3+1] + (targetPositions[k3+1] - startPositions[k3+1]) * easedProgress;
                    currentPositions[k3+2] = startPositions[k3+2] + (targetPositions[k3+2] - startPositions[k3+2]) * easedProgress;
                    currentColors[k3] = startColors[k3] + (targetColors[k3] - startColors[k3]) * easedProgress;
                    currentColors[k3+1] = startColors[k3+1] + (targetColors[k3+1] - startColors[k3+1]) * easedProgress;
                    currentColors[k3+2] = startColors[k3+2] + (targetColors[k3+2] - startColors[k3+2]) * easedProgress;

                    // 3D-Rotation der Sphere anwenden
                    var finalX = currentPositions[k3];
                    var finalY = currentPositions[k3+1];
                    var finalZ = currentPositions[k3+2];

                    var shouldRotate = false;
                    var shouldRotateDNA = false;
                    if ((animationPhase === 'sphere' || animationPhase === 'scatterFromGlobe' || animationPhase === 'scatterFromDNA') && easedProgress >= 0.95) {
                        // Partikel die in der Sphere angekommen sind - drehen sich mit
                        shouldRotate = true;
                    } else if ((animationPhase === 'toShape' || animationPhase === 'toGlobe' || animationPhase === 'toDNA') && easedProgress < 0.05) {
                        // Partikel die noch in der Sphere sind (noch nicht losgeflogen)
                        shouldRotate = true;
                    }

                    // DNA Rotation - waehrend toDNA und fuer noch nicht losgeflogen Partikel bei scatterFromDNA
                    if (animationPhase === 'toDNA') {
                        shouldRotateDNA = true;
                    } else if (animationPhase === 'scatterFromDNA' && easedProgress < 0.05) {
                        // Partikel die noch in der DNA sind drehen sich weiter mit
                        shouldRotateDNA = true;
                    }

                    // Globe Rotation
                    var shouldRotateGlobe = false;
                    if (animationPhase === 'toGlobe') {
                        shouldRotateGlobe = true;
                    } else if (animationPhase === 'scatterFromGlobe') {
                        shouldRotateGlobe = true;
                    }

                    if (shouldRotate) {
                        // Rotation um Y-Achse fÃ¼r 3D-Effekt (Sphere)
                        var relX = currentPositions[k3] - sphereCenterX;
                        var relZ = currentPositions[k3+2];
                        var cosR = Math.cos(sphereRotation);
                        var sinR = Math.sin(sphereRotation);
                        finalX = relX * cosR - relZ * sinR + sphereCenterX;
                        finalZ = relX * sinR + relZ * cosR;
                    }

                    if (shouldRotateGlobe) {
                        // Rotation um Y-Achse fuer Globe Icon
                        var relX = currentPositions[k3] - globeCenterX;
                        var relZ = currentPositions[k3+2];
                        var cosR = Math.cos(globeRotation);
                        var sinR = Math.sin(globeRotation);
                        finalX = relX * cosR - relZ * sinR + globeCenterX;
                        finalZ = relX * sinR + relZ * cosR;
                    }

                    if (shouldRotateDNA) {
                        // Rotation um Y-Achse fÃ¼r DNA Helix MIT Beibehaltung der Schraegstellung
                        var tiltAngle = 0.18; // Gleicher Winkel wie in formDNAHelix
                        var cosT = Math.cos(tiltAngle);
                        var sinT = Math.sin(tiltAngle);

                        // Aktuelle Position relativ zum Zentrum
                        var relX = currentPositions[k3] - dnaCenterX;
                        var relY = currentPositions[k3+1];
                        var relZ = currentPositions[k3+2];

                        // 1. Schraegstellung rueckgaengig machen (un-tilt)
                        var untiltedX = relX * cosT + relY * sinT;
                        var untiltedY = -relX * sinT + relY * cosT;

                        // 2. Rotation um Y-Achse
                        var cosR = Math.cos(dnaRotation);
                        var sinR = Math.sin(dnaRotation);
                        var rotatedX = untiltedX * cosR - relZ * sinR;
                        var rotatedZ = untiltedX * sinR + relZ * cosR;

                        // 3. Schraegstellung wieder anwenden (re-tilt)
                        finalX = rotatedX * cosT - untiltedY * sinT + dnaCenterX;
                        finalY = rotatedX * sinT + untiltedY * cosT;
                        finalZ = rotatedZ;
                    }

                    positionsAttr.array[k3] = finalX;
                    positionsAttr.array[k3+1] = finalY;
                    positionsAttr.array[k3+2] = finalZ;
                    colorsAttr.array[k3] = currentColors[k3];
                    colorsAttr.array[k3+1] = currentColors[k3+1];
                    colorsAttr.array[k3+2] = currentColors[k3+2];

                    sizes[k] = startSizes[k] + (targetSizes[k] - startSizes[k]) * easedProgress;
                    sizesAttr.array[k] = sizes[k];
                }
                positionsAttr.needsUpdate = true;
                colorsAttr.needsUpdate = true;
                sizesAttr.needsUpdate = true;

                
                renderer.render(scene, camera);
            }

            window.addEventListener('resize', function() {
                camera.aspect = window.innerWidth / window.innerHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(window.innerWidth, window.innerHeight);
            });

            animate();
        }
